import React, { Component } from 'react';
import './App.css';
import {Table, Container } from 'react-bootstrap';

class Visualizacao extends Component {
  constructor(props) {
    super(props);

    this.state = {
      valores: [{id:1,nome:"Gabriel",cidade:"Maranguape",idade:22}],
      indice: [1],
      inicio: 0, fim: 10,
    }
  }

  eventoBtn = (opcao) => {
    if(opcao === "First"){
      this.setState({inicio:0,fim:10});
    }
    else if(opcao==="Last"){
      this.setState({fim:this.state.valores.length,inicio:this.state.valores.length-(this.state.valores.length%10),});
    }
    else{
      this.setState({inicio:(opcao*10)-10,fim:opcao*10});
    }
  }
  render() {
    return (
      <div> 
        <Container>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>ID</th>
                <th>NOME</th>
                <th>CIDADE</th>
                <th>IDADE</th>
              </tr>
            </thead>
            <tbody>
              {this.state.valores.filter(
                (valores,index) => index >= this.state.inicio && index < this.state.fim)
                .map((item, indice) =>
                  <tr key={indice}>
                    <td>{item.id}</td>
                    <td>{item.nome}</td>
                    <td>{item.cidade}</td>
                    <td>{item.idade}</td>
                  </tr>
                )}
            </tbody>
          </Table>
          < nav aria-label="Page navigation example">
            <ul className="pagination justify-content-end">
              <li className="page-item" key ="first">
                <button className="page-link" onClick={() =>this.eventoBtn("First")}>First</button>
              </li>
              {this.state.indice.map((item, indice) =>
                <li className="page-item"><button className="page-link" onClick={() =>this.eventoBtn(item)} key ={indice}>{item}</button></li>
              )}
              <li className="page-item" key ="last">
                <button className="page-link" onClick={() =>this.eventoBtn("Last")}>Last</button>
              </li>
            </ul>
          </nav>
        </Container>
      </div >
    );
  }
}

export default Visualizacao;
