import React, { Component } from 'react';
import './App.css';
import { Table, Container, Button } from 'react-bootstrap'

import ModalNew from './Modal'
import Cadastro from './cadastro'
class Visualizacao extends Component {
  constructor(props) {
    super(props)
    this.state = {
      value: false,
      v:null
    }
  }
  trocar() {
    this.setState({ value: true, ...this.state })
  }
  ret = (indice) =>{
    console.log(this.props.users[indice-1])
    this.setState({v : <Cadastro user={this.props.users[indice]} label="Editar Cadastro" />})
  }
  render() {
    return (
      <div>
        <Container>
          <Table striped bordered hover>
            <thead>
              <tr>
                {this.props.colunas.map((item, key) => <th key={key}> {item} </th>)}
              </tr>
            </thead>
            <tbody>
              {this.props.users.filter(
                (valores, index) => index > 0 && index < 10)
                .map((item, indice) =>
                  <tr key={indice}>
                    <td>{item.id}</td>
                    <td>{item.nome}</td>
                    <td>{item.cnpj}</td>
                    <td>{item.insc}</td>
                    <td>{item.latitude}</td>
                    <td>{item.longitude}</td>
                    <td><Button variant="primary" onClick={() => this.ret(indice)} >Primary</Button>{' '}<Button variant="danger" onClick={() => this.props.removeUser(item)}>Remove</Button>
                    </td>
                  </tr>
                )}
            </tbody>
          </Table>
          {this.state.v}
          <ModalNew valor={this.state.value} />
        </Container>
      </div >
    )
  }
}
export default (Visualizacao);
