import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

import Cadastro from './cadastro'
import Visualizacao from './visualizacao';

const urlBase = "http://localhost:3001/users"
const initValue = {
    user: { id: undefined, nome: "", cidade: "", idade: undefined },
    users: [{}],
}
 class UserCrud extends Component {
    constructor(props){
        super(props);
    }
    state = { ...initValue }
    componentWillMount() {
        axios(urlBase).then((response) => {
            this.setState({users: response.data });
        })
    }
    updateValue = (e) => {
        const user = {...this.state.user};
        user[e.target.name] = e.target.value;
        this.setState({user});
    }
    addUser(valor){
        console.log(valor);
    }
    render() {
        return(
            <div>
                {this.props.opcao === 1 ?<Visualizacao users = {this.state.users} 
                colunas={['ID','NAME','CIDADE','IDADE']}/>:<Cadastro  addUser={(valor) => this.addUser(valor)}/>}
            </div>

        )

    }
}

export default UserCrud;
