import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

import Cadastro from './cadastro'
import Visualizacao from './visualizacao';

const urlBase = "http://localhost:3001/users"
const initValue = {
    user: { id: undefined, nome: undefined, cnpj: undefined, insc: undefined, latitude: undefined, longitude: undefined },
    users: [{}],
}
class UserCrud extends Component {
    state = { ...initValue }
    componentWillMount() {
        axios(urlBase).then((response) => {
            this.setState({ users: response.data });
        })
    }
    save(valor) {
        const user = this.state.users
        const metodo = user.id ? 'put' : 'post'
        const url = user.id ? `${urlBase}+/+${user.id}` : urlBase
        user.push(valor)
        this.setState({
            users: user
        })
        axios[metodo](url, valor)
            .then(function (response) {
                console.log(response);
            })
    }
    removeUser = (item) => {
        axios['delete'](urlBase + '/' + item.id).then(function (response) {
        })
        const users = this.state.users.filter(user => user !== item)
        this.setState({ users })
    }
    render() {
        return (
            <div>
                {this.props.opcao === 1 ? <Visualizacao users={this.state.users}
                    colunas={['ID', 'NAME', 'CNPJ', 'INSCRIÇÃO ESTADUAL', 'LATITUDE', 'LONGITUDE']} removeUser={(item) => this.removeUser(item)} save={(valor) => this.save(valor)}/> : <Cadastro save={(valor) => this.save(valor) }
                        indice={this.state.users.length} label = "Cadastro" opcao = 'post' />}
            </div>

        )

    }
}

export default UserCrud;
