import React, {} from 'react';
import '../../App.css';
import { Table, Container, Button } from 'react-bootstrap'

import Pagination from './pagination/pagination'
import ModalNew from './modal/Modal'

let valor=false
let index =0

const Visualizacao  = (props) => {
  const getIndice = (indice) => {
    index = indice
    this.modal(true)
  }

  const modal = (value) => {
    //
    valor = value
  }
    return (
      <div>
        <Container>
          <Table striped bordered hover>
            <thead>
              <tr>
                {props.colunas.map((item, key) => <th key={key}> {item} </th>)}
              </tr>
            </thead>
            <tbody>
              {props.users.map((item, indice) =>
                  <tr key={indice}>
                    <td>{item.id}</td>
                    <td>{item.nome}</td>
                    <td>{item.cnpj}</td>
                    <td>{item.insc}</td>
                    <td>{item.latitude}</td>
                    <td>{item.longitude}</td>
                    <td><Button variant="primary btn btn-sm" onClick={() => getIndice(indice)} >Primary</Button>{' '}
                      <Button variant="danger btn btn-sm" onClick={() => props.removeUser(item)}>Remove</Button></td>
                  </tr>

                )}
            </tbody>
          </Table>
          <Pagination pagination = {(inicio,fim)=>props.pagination(inicio,fim)} usersTotal={props.usersTotal}/>
          <ModalNew user={props.users[index]} modal={(e) => modal(e)} value={valor} save={(v,indice) => this.props.save(v,index)} />
        </Container>
      </div >
    )
  }
export default (Visualizacao);
