import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import { Table, Container } from 'react-bootstrap';

const urlBase = "http://localhost:3001/users"
const initValue = {
    user:{id:undefined,nome:"",cidade:"",idade:undefined},
    users:[{}]
}

class UserCrud extends Component {
    state = {...initValue}
    initT = () =>{
        axios.get(urlBase).then((response) => {
           this.setState({users:response.data});
        })
    }
    tableView() {
        this.initT();
        return (
            <div>
                <Container>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>NOME</th>
                                <th>CIDADE</th>
                                <th>IDADE</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.users.filter(
                                (valores, index) => index >= 0 && index < 10)
                                .map((item, indice) =>
                                    <tr key={indice}>
                                        <td>{item.id}</td>
                                        <td>{item.nome}</td>
                                        <td>{item.cidade}</td>
                                        <td>{item.idade}</td>
                                    </tr>
                                )}
                        </tbody>
                    </Table>
                </Container>
            </div>
        )
    }
    formView() {
        return (
            <div className="centralizar">
                <h2>Cadastro</h2>
                <form >
                    <div className="row">
                        <div className="form-group col-sm-12" >
                            Nome
              <input required type="text" className="form-control" pattern="[a-zA-Z ' ']+" name="nome" onChange={this.props.changeDescription} value={this.props.valores} />
                        </div>
                        <div className="form-group col-sm-12">
                            CNPJ
              <input type="text" className="form-control" placeholder="99.999.999/9999-99" required name="cnpj" />
                        </div>
                        <div className="form-group col-sm-12">
                            Inscrição Estadual
              <input type="text" className="form-control" placeholder="999.999.999" required name="inscricao" />
                        </div>
                        <div className="form-group col-sm-6">
                            Latitude
              <input type="number" className="form-control" placeholder="99.99999999" required
                                name="latitude" />
                        </div>
                        <div className="form-group col-sm-6">
                            Longitude
              <input type="number" className="form-control" placeholder="99.99999999" required
                                name="longitude" />
                        </div>
                    </div>

                </form>
                <button className="btn btn-dark" onClick ={() =>this.initT()}  >Salvar</button>
            </div >
        )
    }
    render() {
        return (
            <div>
                {this.props.valor === "view" ?this.tableView():this.formView()}
            </div>
        )
    }
}


export default (UserCrud);
