import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

import Cadastro from './cadastro'
import Visualizacao from './visualizacao';

const urlBase = "http://localhost:3001/users"
const initValue = {
    user: { id:undefined,nome:undefined,cnpj:undefined,insc:undefined,latitude:undefined,longitude:undefined },
    users: [{}],
}
 class UserCrud extends Component {
    state = { ...initValue }
    componentWillMount() {
        axios(urlBase).then((response) => {
            this.setState({users: response.data });
        })
    }
    addUser(valor){
        const user = this.state.users
        user.push(valor)
        this.setState ({
            users:user
        })
        axios[ 'post'](urlBase,valor)
          .then(function (response) {
            console.log(response);
          })
    }
    removeUser = (valor) =>{
        console.log(valor)
        axios['delete'](this.state.urlBase+'/'+valor).then(function (response) {
            console.log(response);
        })
        this.componentWillMount()
    }
    render() {
        return(
            <div>
                {this.props.opcao === 1 ?<Visualizacao users = {this.state.users} 
                colunas={['ID','NAME','CNPJ','INSCRIÇÃO ESTADUAL','LATITUDE','LONGITUDE']} removeUser = {(valor) => this.removeUser(valor)}/>:<Cadastro  addUser={(valor) => this.addUser(valor)} 
                indice ={this.state.users.length} />}
            </div>

        )

    }
}

export default UserCrud;
