import React, { Component } from 'react';
import './App.css';
import Visualizacao from './visualizacao';

class Cadastro extends Component {
    constructor(props) {
        super(props);

        this.state = {
            valores: [{}],
            indice: [1],
            inicio: 0, fim: 10,
        }
        console.log(this.state.valores)
    }

    eventoBtn = (opcao) => {
        if (opcao === "First") {
            this.setState({ inicio: 0, fim: 10 });
        }
        else if (opcao === "Last") {
            this.setState({ fim: this.state.valores.length, inicio: this.state.valores.length - (this.state.valores.length % 10), });
        }
        else {
            this.setState({ inicio: (opcao * 10) - 10, fim: opcao * 10 });
        }
    }
    add= () => {
        let v = this.state.valores;
        v.push({id:1,nome:"Gabriel",cidade:"Maranguape",idade:22});
        this.setState({valores:v});
    }
    render() {
        return (
            <div className="centralizar">
                <h2>Cadastro</h2>
                <form >
                    <div className="row">
                        <div className="form-group col-sm-12" >
                            Nome
              <input required type="text" className="form-control" pattern="[a-zA-Z ' ']+"  name="nome" />
                        </div>
                        <div className="form-group col-sm-12">
                            CNPJ
              <input type="text" className="form-control" placeholder="99.999.999/9999-99" required  name="cnpj" />
                        </div>
                        <div className="form-group col-sm-12">
                            Inscrição Estadual
              <input type="text" className="form-control" placeholder="999.999.999" required  name="inscricao" />
                        </div>
                        <div className="form-group col-sm-6">
                            Latitude
              <input type="number" className="form-control" placeholder="99.99999999" required
                                 name="latitude" />
                        </div>
                        <div className="form-group col-sm-6">
                            Longitude
              <input type="number" className="form-control" placeholder="99.99999999" required
                                 name="longitude" />
                        </div>
                    </div>
                    <button  className="btn btn-dark" id='btn' onClick ={() => this.add} >Salvar</button>
                </form>
            </div >
        );
    }
}

export default Cadastro;
