import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import { Table, Container } from 'react-bootstrap';
const urlBase = "http://localhost:3001/users"

class UserCrud extends Component {
    constructor(props) {
        super(props);
        this.state = {
            valores: props.valores || [],

        }
    }
    tableForm() {
        return (
            <div>
                <Container>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>NOME</th>
                                <th>CIDADE</th>
                                <th>IDADE</th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.state.valores.filter(
                                (valores, index) => index >= this.state.inicio && index < this.state.fim)
                                .map((item, indice) =>
                                    <tr key={indice}>
                                        <td>{item.id}</td>
                                        <td>{item.nome}</td>
                                        <td>{item.cidade}</td>
                                        <td>{item.idade}</td>
                                    </tr>
                                )}
                        </tbody>
                    </Table>
                </Container>
            </div>
        )
    }
    render() {
        return (
            <div>
                {this.tableForm()}
            </div>
        )
    }
}


export default (UserCrud);
