import React, { Component } from 'react';
import './App.css';
import { Button, Nav, Navbar, Table, Container, Pagination } from 'react-bootstrap';

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      valores: [{ id: 0, nome: "Gabriel", cidade: "Maranguape", idade: 21 },
      { id: 1, nome: "TTTT", cidade: "Maranguape", idade: 22 },
      { id: 2, nome: "TTTT", cidade: "Maranguape", idade: 21 },
      { id: 3, nome: "TTTT", cidade: "Maranguape", idade: 22 }],

      indice: [0, 1, 2],
    }
  }

  eventoBtn = () => {
    let v = this.state.valores;
    v.push({ id: 0, nome: "Gabriel", cidade: "Maranguape", idade: 21 });
    console.log(v.filter((valor) => valor.id > 0 && valor.idade === 22));
    this.setState({ indice: this.state.indice + 1, valores: v });
  }
  render() {
    return (
      <div className="App">
        <Navbar bg="dark" variant="dark" expand="lg">
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link href="#home">Home</Nav.Link>
              <Nav.Link href="#features">Features</Nav.Link>
            </Nav>
          </Navbar.Collapse>

        </Navbar>
        <br />
        <Container>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>ID</th>
                <th>NOME</th>
                <th>CIDADE</th>
                <th>IDADE</th>
              </tr>
            </thead>
            <tbody>
              {this.state.valores.map(
                (item, indice) => <tr key={indice}>
                  <td>{this.state.valores[indice].id}</td>
                  <td>{this.state.valores[indice].nome}</td>
                  <td>{this.state.valores[indice].cidade}</td>
                  <td>{this.state.valores[indice].idade}</td>
                </tr>
              )}
            </tbody>

          </Table>
          <Pagination>
            <Pagination.First />
            <Pagination.Item>{}</Pagination.Item>
            <Pagination.Last />
          </Pagination>
          <nav aria-label="Page navigation example">
            <ul className="pagination justify-content-end">
              <li className="page-item disabled">
                <a className="page-link">Previous</a>
              </li>
              <li className="page-item"><a class="page-link" >1</a></li>
              <li className="page-item"><a class="page-link" >2</a></li>
              <li className="page-item"><a class="page-link" >3</a></li>
              <li className="page-item">
                <a className="page-link">Next</a>
              </li>
            </ul>
          </nav>
        </Container>

        <Button variant="dark" onClick={() => this.eventoBtn()}>Dark</Button>
      </div>
    );
  }
}

export default App;
