import React, { Component } from 'react';
import './App.css';
import {Table, Container } from 'react-bootstrap';
import {connect} from 'react-redux'

class Visualizacao extends Component {
  // constructor(props) {

  //   this.state = {
  //     indice: [1],
  //     inicio: 0, fim: 10
  //   }
  // }

  // eventoBtn = (opcao) => {
