import React, { Component } from 'react';
import './App.css';


class Cadastro extends Component {
    constructor(props) {
        super(props);

        this.state = {
            valores: [{}],
            indice: [1],
            inicio: 0, fim: 10,
        }
    }

    eventoBtn = (opcao) => {
        if (opcao === "First") {
            this.setState({ inicio: 0, fim: 10 });
        }
        else if (opcao === "Last") {
            this.setState({ fim: this.state.valores.length, inicio: this.state.valores.length - (this.state.valores.length % 10), });
        }
        else {
            this.setState({ inicio: (opcao * 10) - 10, fim: opcao * 10 });
        }
    }
    render() {
        return (
            <div>
                <h2>Cadastro</h2>
                <form method="POST" >
                    <div className="row">
                        <div className="form-group col-sm-12" >
                            Nome
              <input required type="text" className="form-control" pattern="[a-zA-Z ' ']+" maxlength="50" minlength="15" name="nome" />
                        </div>
                        <div className="form-group col-sm-12">
                            CNPJ
              <input type="text" className="form-control" placeholder="99.999.999/9999-99" required maxlength="18" minlength="18" name="cnpj" />
                        </div>
                        <div className="form-group col-sm-12">
                            Inscrição Estadual
              <input type="text" className="form-control" placeholder="999.999.999" required minlength="11" maxlength="11" name="inscricao" />
                        </div>
                        <div className="form-group col-sm-6">
                            Latitude
              <input type="number" className="form-control" placeholder="99.99999999" required
                                required minlength="1" maxlength="11" name="latitude" />
                        </div>
                        <div className="form-group col-sm-6">
                            Longitude
              <input type="number" className="form-control" placeholder="99.99999999" required
                                minlength="1" maxlength="11" name="longitude" />
                        </div>
                    </div>
                    <button type="submit" className="btn btn-dark" id='btn'  >Salvar</button>
                </form>
            </div >
        );
    }
}

export default Cadastro;
