import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
import { Table, Container } from 'react-bootstrap';

import Cadastro from './cadastro'
import Visualizacao from './visualizacao';

const urlBase = "http://localhost:3001/users"
const initValue = {
    user: { id: undefined, nome: "", cidade: "", idade: undefined },
    users: [{}]
}
export default class UserCrud extends Component {
    state = { ...initValue }
    componentWillMount() {
        axios(urlBase).then((response) => {
            this.setState({users: response.data });
        })
    }
    updateValue = (e) => {
        const user = {...this.state.user};
        user[e.target.name] = e.target.value;
        this.setState({user});
    }
    render() {
        return (
            <div>
                {this.props.valor === "view" ? <Cadastro/>  : <Visualizacao valores = {this.state.users}/> }
            </div>
        )
    }
}
