import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';


import Cadastro from './cadastro/cadastro'
import Visualizacao from './visualizacao/visualizacao';

const urlBase = "http://localhost:3001/users"
const initValue = {
    user: { id: undefined, nome: undefined, cnpj: undefined, insc: undefined, latitude: undefined, longitude: undefined },
    users: [{}],
    url:"http://localhost:3001/users?_page=1&_limit=10",
    usersTotal:[{}],
    inicio:1,
    fim:10
}
class UserCrud extends Component {
    state = { ...initValue}
    componentWillMount() {
        axios(this.state.url).then((response) => {
        this.setState({ users: response.data });
        console.log(response)
    })
        axios(urlBase).then((response) => {
            this.setState({ usersTotal: response.data });
        })
    }
    save(valor) {
        let usersTotal = this.state.usersTotal
        const metodo = valor.id!==this.state.usersTotal.length ? 'put' : 'post'
        const url = valor.id!==this.state.usersTotal.length ? `${urlBase}/${valor.id}` : urlBase
        axios[metodo](url, valor).then(function (response) {
                console.log(response);
        })
        const indice = this.state.users.indexOf(valor.nome)
        console.log(indice)
        usersTotal = valor.id!==this.state.usersTotal.length ? this.state.usersTotal : usersTotal.push(usersTotal)
        this.setState({usersTotal,users:this.state.users})
        this.updateTable(this.state.url)
    }
    removeUser = (item) => {
        axios['delete'](urlBase + '/' + item.id).then(function (response) {
        })
        const usersTotal = this.state.usersTotal.filter(user => user !== item)
        this.setState({ usersTotal })
    }
    pagination(inicio,fim){
        const url = `http://localhost:3001/users?_page=${inicio}&_limit=${fim}`
        this.setState({inicio,fim})
        this.updateTable(url)
    }
    updateTable(url){
        axios['get'](url).then((response) => {
            this.setState({ users: response.data,url});
        });
    }
    render() {
        return (
            <div>
                {this.props.opcao === 1 ? <Visualizacao pagination = {(inicio,fim)=>this.pagination(inicio,fim)} users = {this.state.users} usersTotal={this.state.usersTotal}
                    colunas={['ID', 'NAME', 'CNPJ', 'INSCRIÇÃO ESTADUAL', 'LATITUDE', 'LONGITUDE']} removeUser={(item) => this.removeUser(item)} save={(valor) => this.save(valor)}/> : <Cadastro save={(valor) => this.save(valor) }
                        indice={this.state.users.length} label = "Cadastro" />}
            </div>

        )

    }
}

export default UserCrud;
