import React, { Component } from 'react';
import '../../App.css';
import { Table, Container, Button } from 'react-bootstrap'

import Pagination from './pagination/pagination'
import ModalNew from './modal/Modal'

const initValue = {
      value: false,
      indice: 0
}
class Visualizacao extends Component {
  state ={initValue}
  getIndice = (indice) => {
    this.setState({ indice: indice })
    this.modal(true)
  }

  modal = (value) => {
    //
    this.setState({ value: value })
  }
  render() {
    return (
      <div>
        <Container>
          <Table striped bordered hover>
            <thead>
              <tr>
                {props.colunas.map((item, key) => <th key={key}> {item} </th>)}
              </tr>
            </thead>
            <tbody>
              {this.props.users.map((item, indice) =>
                  <tr key={indice}>
                    <td>{item.id}</td>
                    <td>{item.nome}</td>
                    <td>{item.cnpj}</td>
                    <td>{item.insc}</td>
                    <td>{item.latitude}</td>
                    <td>{item.longitude}</td>
                    <td><Button variant="primary btn btn-sm" onClick={() => this.getIndice(indice)} >Primary</Button>{' '}
                      <Button variant="danger btn btn-sm" onClick={() => this.props.removeUser(item)}>Remove</Button></td>
                  </tr>

                )}
            </tbody>
          </Table>
          <Pagination pagination = {(inicio,fim)=>this.props.pagination(inicio,fim)} usersTotal={this.props.usersTotal}/>
          <ModalNew user={this.props.users[this.state.indice]} modal={(e) => this.modal(e)} value={this.state.value} save={(valor,indice) => this.props.save(valor,this.state.indice)} />
        </Container>
      </div >
    )
  }
}
export default (Visualizacao);
