import React, { Component } from 'react';
import './App.css';
import { Nav, Navbar } from 'react-bootstrap';
import { Link } from "react-router-dom";
import { Route, Switch, Redirect } from "react-router-dom";
import Cadastro from './cadastro';
import Visualizacao from './visualizacao';
import UserCrud from './UserCrud'
class App extends Component {
  constructor(props){
    super(props)
    this.state = {
      opcao:"visualizacao"
    }
  }
  trocarTela = (op) =>{
    this.setState({
      opcao:op
    })
  }

  render() {
    return (
      <div>
        <div className="App">
          <Navbar bg="dark" variant="dark" expand="lg">
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mr-auto">
                <Nav.Link to="/" className="nav-link" onClick = {() => this.trocarTela("visualizacao")}>Visualizacao</Nav.Link>
                <Nav.Link to="/cadastro" className="nav-link" onClick = {() => this.trocarTela("cadastrar")}>Cadastro</Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
          <br />
          <UserCrud opcao = {this.state.op}/>
        </div >

        {/* <Switch>
          <Route exact path="/" component={Visualizacao} />
          <Route path="/cadastro" component={Cadastro} />
          <Redirect from = "*" to = "/"/>
        </Switch> */}
      </div>
    );
  }
}


export default App;
